import { Component } from 'react';

import AppInfo from '../app-info/app-info';
import SearchPanel from '../search-panel/search-panel';
import AppFilter from '../app-filter/app-filter';
import EmployeesList from '../employees-list/employees-list';
import EmployeesAddForm from '../employees-add-form/employees-add-form';

import './app.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [
        {name: "Alex.P", salary: 800, increase: false, rise: true, id: 1},
        {name: "Vladimir.S", salary: 1200, increase: true, rise: false, id: 2},
        {name: "Phedor.T", salary: 15000, increase: true, rise: false, id: 3},
      ]
    }
    this.maxId = 4;
  }

  deleteItem = (id) => {
    this.setState(({data}) => {

        return {
          data: data.filter(item => item.id !== id) // фильтрует данные и возвращает новый массив
        }
    })
  }

  addItem = (name, salary) => {
    const newItem = {
      name,
      salary,
      increase: false,
      rise: false,
      id: this.maxId++
    }
    this.setState(({data}) => {
      const newArr = [...data, newItem];
      return {
        data: newArr
      }
    });
  }

  onToggleIncrease = (id) => {
      this.setState(({data}) => ({
          data: data.map(item => {
            if (item.id === id) {
              return {...item, increase: !item.increase}
            }
            return item;
          })
    }))
  }

  onToggleRise = (id) => {
        this.setState(({data}) => ({
          data: data.map(item => {
            if (item.id === id) {
              return {...item, rise: !item.rise}
            }
            return item;
          })
    }))
  }

  render () {
    const employees = this.state.data.length;
    const increased = this.state.data.filter(item => item.increase).length;
    return (
      <div className="app">
          <AppInfo employees={employees} increased={increased}/>

          <div className="search-panel">
              <SearchPanel/>
              <AppFilter/>
          </div>

          <EmployeesList 
              data={this.state.data}
              onDelete={this.deleteItem}
              onToggleIncrease={this.onToggleIncrease}
              onToggleRise={this.onToggleRise}/>
          <EmployeesAddForm onAdd={this.addItem}/>
      </div>
    );
  }
}


export default App;
